function play2 () {
    current_level = 0
    lock = false
    effects.starField.startScreenEffect()
    levels2 = [
    tiles.createMap(tilemap`level2 1`),
    tiles.createMap(tilemap`level2 2`),
    tiles.createMap(tilemap`level2 3`),
    tiles.createMap(tilemap`level2 4`),
    tiles.createMap(tilemap`leve2 5`),
    tiles.createMap(tilemap`level40`)
    ]
    info.setLife(3)
    info.startCountdown(120)
    initLevel(false)
    initPlayers()
}
scene.onOverlapTile(SpriteKind.Player, assets.tile`myTile1`, function (sprite, location) {
    tiles.replaceAllTiles(assets.tile`myTile1`, assets.tile`myTile9`)
})
scene.onHitWall(SpriteKind.Player, function (sprite, location) {
    if (game3 == 2) {
        if (sprite.tileKindAt(TileDirection.Bottom, assets.tile`myTile2`) || sprite.tileKindAt(TileDirection.Bottom, assets.tile`myTile4`)) {
            sprite.vx = 0
        } else {
            if (tiles.tileIs(location, assets.tile`myTile2`) || tiles.tileIs(location, assets.tile`myTile4`)) {
                if (sprite.tileKindAt(TileDirection.Right, assets.tile`myTile2`) || sprite.tileKindAt(TileDirection.Right, assets.tile`myTile4`)) {
                    sprite.vx = -100
                } else if (sprite.tileKindAt(TileDirection.Left, assets.tile`myTile2`) || sprite.tileKindAt(TileDirection.Left, assets.tile`myTile4`)) {
                    sprite.vx = 100
                }
            }
        }
    }
})
sprites.onDestroyed(SpriteKind.WallUpdateTrigger, function (sprite) {
    scheduleWallUpdate()
})
