function initLevel (useSavedSpawnPoints: boolean) {
    button_presses = 0
    if (current_level < levels2.length) {
        tiles.loadMap(levels2[current_level])
    } else {
        game.gameOver(true)
    }
    if (!(useSavedSpawnPoints)) {
        Lemon_spawn_location = tiles.getTilesByType(assets.tile`myTile7`)[0]
        Tea_spawn_location = tiles.getTilesByType(assets.tile`myTile6`)[0]
    } else {
        tiles.setTileAt(Lemon_spawn_location, assets.tile`myTile7`)
        tiles.setTileAt(Tea_spawn_location, assets.tile`myTile6`)
    }
    orange_wall_locations = tiles.getTilesByType(assets.tile`myTile3`)
    orange_reverse_wall_locations = tiles.getTilesByType(assets.tile`myTile5`)
    orange_wall_extension_height = 0
    for (let index = 0; index < 3; index++) {
        orange_wall_extension_height += 1
        updateWalls(orange_wall_extension_height, true)
    }
}
